mapper.select:
/* selectById */
SELECT

FROM
    T_ACC_CURRENCY
WHERE
    ID = #{id,jdbcType=VARCHAR}

/* selectByCurrencySymbol */ SELECT
FROM T_ACC_CURRENCY WHERE CURRENCY_CODE = #{currencyCode,jdbcType=VARCHAR} AND
EXPIRY_DATE >= CAST(#{currentDate,jdbcType=DATE} AS DATE)

mapper.update:
/* update */
UPDATE
    T_ACC_CURRENCY
SET
    UPDATED_AT = SYSDATE,
    UPDATED_BY = #{updatedBy,jdbcType=VARCHAR}

        , CURRENCY_SYMBOL = #{currencySymbol,jdbcType=VARCHAR}


        , CURRENCY_CODE = #{currencyCode,jdbcType=VARCHAR}


        , CURRENCY_NAME = #{currencyName,jdbcType=VARCHAR}


        , EFFECTIVE_DATE = #{effectiveDate,jdbcType=TIMESTAMP}


        , EXPIRY_DATE = #{expiryDate,jdbcType=TIMESTAMP}


        , CONTURY = #{contury,jdbcType=VARCHAR}


        , DECIMAL_DIGITS = #{decimalDigits,jdbcType=DECIMAL}

WHERE
    ID = #{id,jdbcType=DECIMAL}
